const CellLogic = (rowId) => {
  const table = document.getElementById('DataTable');
  const rows = table.querySelectorAll(`tr#${rowId}`);

  rows.forEach((row) => {
      const cols = row.querySelectorAll('td');
      let selectCC = 0, selectCT = 0;

      cols.forEach((col, j) => {
          const select = col.querySelectorAll('select, input');

          if (j === 7 && select[0].value === 'Yes') {
              selectCC = 1;
          }

          if (j === 8 && select[0].value === 'No') {
              selectCT = 1;
          }
      });

      console.log(selectCC, selectCT);

      cols.forEach((col, j) => {
          const select = col.querySelectorAll('select, input');

          if ((j === 5 || j === 6) && selectCC === 1 && selectCT === 1) {
              select[0].disabled = true;
          }
      });
  });
};

